!function (e) {
    "use strict";
    e('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function () {
        if (location.pathname.replace(/^\//, "") == this.pathname.replace(/^\//, "") && location.hostname == this.hostname) {
            var t = e(this.hash);
            if ((t = t.length
                ? t
                : e("[name=" + this.hash.slice(1) + "]")).length) 
                return e("html, body").animate({
                    scrollTop: t
                        .offset()
                        .top - 48
                }, 1e3, "easeInOutExpo"),
                !1
        }
    }),
    e(".js-scroll-trigger").click(function () {
        e(".navbar-collapse").collapse("hide")
    }),
    e("body").scrollspy({target: "#mainNav", offset: 54});
    var t = function () {
        e("#mainNav")
            .offset()
            .top > 100
            ? e("#mainNav").addClass("navbar-shrink")
            : e("#mainNav").removeClass("navbar-shrink")
    };
    t(),
    e(window).scroll(t)
}(jQuery);

function thanosClick() {

    //assign a random number
    var myNum = Math.floor(Math.random() * 99999999);

    //fill an array with xx numbers
    var arr = [];
    for (var i = 1; i < 99999999; i++) {
        arr.push(i);
    }

    //divide the array length in half.
    var arrLength = arr.length / 2;

    // fill an array up to half the length with random numbers from the starting
    // array.
    var half = []
    for (i = 1; i < arrLength; i++) {
        half.push(Math.floor(Math.random() * arr.length));
    }

    // logging for testing console.log("random starting number: " + myNum)
    // console.log("starting array: " + arr); console.log("array length: " +
    // arr.length) console.log("array length/2: " + arrLength);
    // console.log(arrLength + " random numbers: " + half); check to see if myNum is
    // in the half array. if true = eliminated // if false = safe

    var vanished = half.includes(myNum)
    //console.log(vanished) //true or false
    
    if (vanished == true) {
        //console.log("thanos killed you");
        $("#result").html("<h2>Thanos Killed You</h2>")
                       
    }else {
        //console.log("you lived");
        $("#result").html("<h2>You are still alive</h2>")
    }

}